mapper.select:
  	select 
    
    from buss_customer_ where 1=1
    	
			and cus_desc_ = #{cusDesc}
		
    	
			and update_time_ = #{updateTime}
		
    	
			and cus_type_ = #{cusType}
		
    	
			and ck_status_ = #{ckStatus}
		
    	
			and cus_name_ = #{cusName}
		
    	
			and update_user_ = #{updateUser}
		
    	
			and cus_loc_ = #{cusLoc}
		
    	
			and create_user_ = #{createUser}
		
    	
			and cus_create_time_ = #{cusCreateTime}
		
    	
			and cus_addr_ = #{cusAddr}
		
    	
			and flg_update_time_ = #{flgUpdateTime}
		
    	
			and cus_cha_ = #{cusCha}
		
    	
			and customer_id_ = #{customerId}
		
    	
			and create_time_ = #{createTime}
		
		order by update_time_ DESC

  	select 
    
    from buss_customer_ where 1=1
    	
			and cus_desc_ = #{cusDesc}
		
    	
			and update_time_ = #{updateTime}
		
    	
			and cus_type_ = #{cusType}
		
    	
			and ck_status_ = #{ckStatus}
		
    	
			and cus_name_ = #{cusName}
		
    	
			and update_user_ = #{updateUser}
		
    	
			and cus_loc_ = #{cusLoc}
		
    	
			and create_user_ = #{createUser}
		
    	
			and cus_create_time_ = #{cusCreateTime}
		
    	
			and cus_addr_ = #{cusAddr}
		
    	
			and flg_update_time_ = #{flgUpdateTime}
		
    	
			and cus_cha_ = #{cusCha}
		
    	
			and customer_id_ = #{customerId}
		
    	
			and create_time_ = #{createTime}
		
		order by update_time_ DESC

SELECT a.customer_id_, a.cus_name_, a.cus_cha_, a.cus_type_, a.cus_loc_, a.ck_status_, ifnull(b.count,0) as crt_qua_ FROM buss_customer_ a LEFT JOIN ( SELECT customer_id_,COUNT(*) AS count FROM buss_transaction_info_ where tx_status_='50' GROUP BY customer_id_ ) b ON a.customer_id_ = b.customer_id_ where a.create_user_ = #{createUser}
and a.cus_name_ like "%" #{cusName} "%"
and a.cus_cha_ = #{cusCha}
and a.cus_type_ = #{cusType}
and a.customer_id_ = #{customerId}
order by a.ck_status_ ASC, a.update_time_ DESC

SELECT a.customer_id_, a.cus_name_, a.cus_cha_, a.cus_type_, a.cus_loc_, a.ck_status_, ifnull(b.count,0) as crt_qua_ FROM buss_customer_ a LEFT JOIN ( SELECT customer_id_,COUNT(*) AS count FROM buss_transaction_info_ where tx_status_='50' GROUP BY customer_id_ ) b ON a.customer_id_ = b.customer_id_ where a.create_user_ in ( select s1.member_id_ from sys_member_ s1 LEFT JOIN sys_member_ s2 on s1.dept_id_ =s2.dept_id_ where s2.member_id_ = #{createUser})
and a.cus_name_ like "%" #{cusName} "%"
and a.cus_cha_ = #{cusCha}
and a.cus_type_ = #{cusType}
and a.customer_id_ = #{customerId}
order by a.ck_status_ ASC , a.update_time_ DESC

SELECT a.customer_id_, a.cus_name_, a.cus_cha_, a.cus_type_, a.cus_loc_, a.ck_status_, ifnull(b.count,0) as crt_qua_ FROM buss_customer_ a LEFT JOIN ( SELECT customer_id_,COUNT(*) AS count FROM buss_transaction_info_ where tx_status_='50' GROUP BY customer_id_ ) b ON a.customer_id_ = b.customer_id_ where 1=1 and a.ck_status_ != '00'
and a.cus_name_ like "%" #{cusName} "%"
and a.cus_cha_ = #{cusCha}
and a.cus_type_ = #{cusType}
and a.customer_id_ = #{customerId}
order by a.ck_status_ ASC , a.update_time_ DESC
